                        <source srcset="images/afbdiw-lzwk-285.jpg 1x, images/afbdiw-lzwk-570.jpg 2x">
                        <img src="images/afbdiw-lzwk-285.jpg" alt="" class="js12 i13">
                    </picture>
                </div>
                <div class="v2 ps74 s74 c68">
                    <picture class="i14">
                        <source srcset="images/9o8ydygtt64-285.jpg 1x, images/9o8ydygtt64-570.jpg 2x">
                        <img src="images/9o8ydygtt64-285.jpg" alt="" class="js13 i13">
                    </picture>
                </div>
            </div>
